import { Component, OnInit } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { Observable, of } from 'rxjs';
import { catchError } from 'rxjs/operators';
import { ErrorDialogComponent } from '../../shared/components/error-dialog/error-dialog.component';

import { Course } from './model/course';
import { CoursesService } from './services/courses.service';

@Component({
  selector: 'app-courses',
  templateUrl: './courses.component.html',
  styleUrls: ['./courses.component.scss'],
})
export class CoursesComponent implements OnInit {
  // courses: Course[] = [{ _id: '1', name: 'Angular', category: 'Front-end' }];
  // courses: Course[] = [];
  courses$: Observable<Course[]>;
  displayedColumns: string[] = ['id', 'name', 'category'];

  constructor(private courseService: CoursesService, public dialog: MatDialog) {
    // this.courses = [];
    this.courses$ = this.courseService.readAll().pipe(
      catchError((error) => {
        this.onError('Erro ao carregar cursos...');
        return of([]);
      })
    );
  }

  ngOnInit(): void {
    // this.readAll();
  }

  // readAll = () => {
  //   this.courseService.readAll().subscribe(course => { this.courses = courses});
  // };

  onError = (errorMessage: string) => {
    this.dialog.open(ErrorDialogComponent, {
      data: errorMessage,
    });
  };
}
<mat-card>
  <mat-toolbar color="primary"> Cursos disponíveis </mat-toolbar>

  <div *ngIf="courses$ | async as courses; else isLoading">
    <table mat-table [dataSource]="courses" class="mat-elevation-z8">
      <!--- Note that these columns can be defined in any order.
            The actual rendered columns are set as a property on the row definition" -->

      <!-- Id Column -->
      <ng-container matColumnDef="id">
        <th mat-header-cell *matHeaderCellDef>Id</th>
        <td mat-cell *matCellDef="let course">{{ course._id }}</td>
      </ng-container>

      <!-- Name Column -->
      <ng-container matColumnDef="name">
        <th mat-header-cell *matHeaderCellDef>Name</th>
        <td mat-cell *matCellDef="let course">{{ course.name }}</td>
      </ng-container>

      <!-- Category Column -->
      <ng-container matColumnDef="category">
        <th mat-header-cell *matHeaderCellDef>Categoria</th>
        <td mat-cell *matCellDef="let course">
          {{ course.category }}
          <mat-icon aria-hidden="false" aria-label="Categoria do curso">{{
            course.category | category
          }}</mat-icon>
        </td>
      </ng-container>

      <!-- Icon Column -->
      <!-- <ng-container matColumnDef="icon">
        <th mat-header-cell *matHeaderCellDef>Categoria</th>
        <td mat-cell *matCellDef="let course">{{ course.category }}</td>
      </ng-container> -->

      <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
      <tr mat-row *matRowDef="let row; columns: displayedColumns"></tr>
    </table>
  </div>

  <ng-template #isLoading>
    <div class="loading-spinner">
      <mat-spinner></mat-spinner>
    </div>
  </ng-template>
</mat-card>
